import React, { useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";

function App() {
  // useStates used to set the firstName, lastName variables
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [nameList, setNameList] = useState([]);


  // useEffect displays the data in the database back to the user in a list
  useEffect(() => {
    Axios.get("http://localhost:3001/api/get").then((response) => {
      setNameList(response.data);
    });
  }, []);

  // this is the function called when the "Submit Name" button is clicked
  const submitName = () => {
    Axios.post("http://localhost:3001/api/insert", {
      firstName: firstName,
      lastName: lastName,
    }).then(() => {
      alert("Successful Insert");
    }); // then() function does not seem to work. I never see an alert that says "Successful Insert"
  };
  return (
    <div className="App">
      <h1>Unknown Concepts</h1>

      <div className="form">
        <label>First Name</label>
        <input
          type="text"
          name="firstName"
          onChange={(e) => {
            setFirstName(e.target.value); // takes the input from the user and setFirstName sets firstName to the user input
          }}
        />
        <label>Last Name</label>
        <input
          type="text"
          name="lastName"
          onChange={(e) => {
            setLastName(e.target.value); // takes the input from the user and setLastName sets lastName to the user input
          }}
        />

        <button onClick={submitName}>Submit Name</button> 

        {nameList.map((value) => { // maps the data in the table Name to the nameList useEffect
          return (
            <h1>
              First Name: {value.firstName} <br /> Last Name: {value.lastName}
            </h1>
          );
        })}
      </div>
    </div>
  );
}

export default App;
