import React, { useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";

function App() {
  // useStates used to set the firstName, lastName variables
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [nameList, setNameList] = useState([]);

  // useEffect displays the data in the database back to the user in a list
  useEffect(() => {
    Axios.get("http://localhost:3001/api/get").then((response) => {
      setNameList(response.data);
    });
  }, []);

  // this is the function called when the "Submit Name" button is clicked
  const submitName = () => {
    Axios.post("http://localhost:3001/api/insert", {
      firstName: firstName,
      lastName: lastName,
    }).then(() => {
      alert("Successful Insert");
    }); // then() function does not seem to work. I never see an alert that says "Successful Insert"
  };
  return (
    <div className="App">
      <div className="registration">
        <h1>Registration</h1>
        <input type="text" placeholder="Username" />
        <input type="text" placeholder="Password" />
        <button>Register</button>
      </div>
      <div className="login">
        <h1>Login</h1>
        <input type="text" placeholder="Username" />
        <input type="text" placeholder="Password" />
        <button>Login</button>
      </div>
    </div>
  );
}

export default App;
